import Layout from "../component/Layout";
import Head from 'next/head';
import IntroPage from "../component/Home/Intro";
import Ourservice from "../component/Home/Ourservice";
import fetch from "isomorphic-unfetch";


function HomePage(props) {
    
    return (
        <Layout>
            <Head>
                <title>Techronx IT Solution and Services</title>
                <meta name="viewport" content="width=device-width, initial-scale=1.0"></meta>
            </Head>
            <div>
                <IntroPage />
                {
                    props.data.map((index, service) => (
                        <Ourservice index={index}  description={service.description} heading={service.heading} id={service.id}/>
                    ))
                }
            </div>


    <style jsx global>{`
       body {
        margin: 0;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
          'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
          sans-serif;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
      }
    `}</style>
        </Layout>
    )
  }

  HomePage.getInitialProps = async function() {
    const res = await fetch('https://backend.techronx.com/api/service');
    const data = await res.json();
    return {
        data
    }
}
  
export default HomePage;
