import React,{Component} from 'react';
import Button from '@material-ui/core/Button';
import Head from "next/head";
import styles from "./css/intro.module.css";
import TransitionsModal from '../tools/Modal';




class IntroPage extends Component{
    constructor (props) {
        super(props);
        this.state = {
            isOpen:false
        }
    }

    componentDidMount(){

        const visited = localStorage.getItem('visited');
        if(visited){
            this.setState({
                isOpen:false
            })
        }
        else{

           const timer =  setInterval(() => {
                this.setState({
                    isOpen:true
                })
            }, 10000)

            setTimeout(() => {
                clearInterval(timer);
            }, 150000);
     
            
                
           
            
            localStorage.setItem('visited', true);
          


        }


        
    }
  
    render(){
        return (
            <div>
              <Head>
                <meta name="viewport" content="width=device-width,minimum-scale=0.5,initial-scale=1"></meta>
              </Head>
              <TransitionsModal isOpen={this.state.isOpen}>
                 <div style={{display:'flex', flexDirection:'column', width:'300px',height:'300px',justifyContent:'space-around', alignItems:'center'}}>
                    <img src="/images/15.gif" width="300px" alt="Digital Marketing Techronx" />
                    <span style={{color:'green'}}>Thank you</span>
                 </div>
              </TransitionsModal>
            <div className={styles.aboutus}>
                <div className={styles.introdescription}>
                    <div className={styles.introhead}>
                        TechronX - IT Solutions <br />
                        and Services.

                        <div className={styles.introbio}>
                        TechronX constantly advances to provide innovative IT solutions in
                        order to make better future by improving your business.
                        </div>
                    </div>
                    <a href="/contact" className={styles.button} >
                        Contact Us
                    </a>
                    
                </div>
                <div className={styles.intrimage}>
                    
                        <img src="/images/intro.gif" alt="techronx" />
                </div>
            </div>
            <div className={styles.border}></div>

        
            </div>
        )
    }
}

export default IntroPage;
